import React, { useRef, useState, useEffect } from 'react';
import { Camera } from 'lucide-react';

const SkinToneAnalyzer = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [analysis, setAnalysis] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Initialize webcam
  useEffect(() => {
    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: { 
            width: 640, 
            height: 480,
            facingMode: 'user' // This ensures we use the front camera on mobile devices
          } 
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error("Error accessing webcam:", err);
      }
    };
    startVideo();
  }, []);

  const analyzeSkinTone = () => {
    setIsAnalyzing(true);
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Save the current context state
    context.save();

    // Mirror the canvas context
    context.scale(-1, 1);
    context.translate(-canvas.width, 0);

    // Draw the mirrored frame
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Restore the context to its original state
    context.restore();

    // Get image data from the center
    const imageData = context.getImageData(
      canvas.width / 2 - 50,
      canvas.height / 2 - 50,
      100,
      100
    );

    // Calculate average RGB values from sampled area
    let r = 0, g = 0, b = 0;
    for (let i = 0; i < imageData.data.length; i += 4) {
      r += imageData.data[i];
      g += imageData.data[i + 1];
      b += imageData.data[i + 2];
    }
    const pixels = imageData.data.length / 4;
    r = r / pixels;
    g = g / pixels;
    b = b / pixels;

    // Convert RGB to Lab
    let rgb = [r/255, g/255, b/255];
    rgb = rgb.map(v => v > 0.04045 ? Math.pow((v + 0.055) / 1.055, 2.4) : v / 12.92);

    const X = rgb[0] * 0.4124 + rgb[1] * 0.3576 + rgb[2] * 0.1805;
    const Y = rgb[0] * 0.2126 + rgb[1] * 0.7152 + rgb[2] * 0.0722;
    const Z = rgb[0] * 0.0193 + rgb[1] * 0.1192 + rgb[2] * 0.9505;

    const Xn = 0.95047, Yn = 1.00000, Zn = 1.08883;
    const xyz = [X/Xn, Y/Yn, Z/Zn].map(v => 
      v > 0.008856 ? Math.pow(v, 1/3) : (7.787 * v) + 16/116
    );

    const L = (116 * xyz[1]) - 16;
    const aValue = 500 * (xyz[0] - xyz[1]);
    const bValue = 200 * (xyz[1] - xyz[2]);

    const hueAngle = Math.atan2(bValue, aValue) * (180/Math.PI);
    let undertone = '';
    if (hueAngle > 60) {
      undertone = 'Warm';
    } else if (hueAngle < 60) {
      undertone = 'Cool';
    } else {
      undertone = 'Neutral';
    }

    let lightness = '';
    if (L > 66) {
      lightness = 'High';
    } else if (L > 45) {
      lightness = 'Medium';
    } else {
      lightness = 'Low';
    }

    let seasons = [];
    if (undertone === 'Warm') {
      if (lightness === 'High') {
        seasons = ['Spring', 'Autumn'];
      } else {
        seasons = ['Autumn'];
      }
    } else if (undertone === 'Cool') {
      if (lightness === 'High') {
        seasons = ['Summer', 'Autumn', 'Winter'];
      } else {
        seasons = ['Summer', 'Autumn'];
      }
    } else {
      if (lightness === 'High') {
        seasons = ['Spring', 'Summer', 'Autumn', 'Winter'];
      } else {
        seasons = ['Summer', 'Autumn'];
      }
    }

    setAnalysis({
      undertone,
      lightness,
      seasons,
      labValues: { L: L.toFixed(1), a: aValue.toFixed(1), b: bValue.toFixed(1) }
    });
    setIsAnalyzing(false);
  };

  return (
    <div className="p-4">
      <div className="mb-4">
        <h2 className="text-2xl font-bold mb-2">Skin Tone Analyzer</h2>
        <p className="text-gray-600">Position your face in the center of the frame</p>
      </div>

      <div className="relative mb-4">
        <video 
          ref={videoRef}
          autoPlay 
          playsInline
          className="w-full max-w-2xl border rounded transform scale-x-[-1]" // Mirror the video
        />
        <canvas 
          ref={canvasRef} 
          width="640" 
          height="480" 
          className="hidden"
        />
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <div className="w-[100px] h-[100px] border-2 border-white rounded-lg opacity-50" />
        </div>
      </div>

      <button
        onClick={analyzeSkinTone}
        disabled={isAnalyzing}
        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded flex items-center gap-2"
      >
        <Camera size={20} />
        {isAnalyzing ? 'Analyzing...' : 'Analyze Skin Tone'}
      </button>

      {analysis && (
        <div className="mt-4 p-4 border rounded">
          <h3 className="font-bold mb-2">Analysis Results:</h3>
          <div className="space-y-2">
            <p>Undertone: <span className="font-medium">{analysis.undertone}</span></p>
            <p>Lightness: <span className="font-medium">{analysis.lightness}</span></p>
            <p>Lab Values: <span className="font-medium">
              L*: {analysis.labValues.L}, 
              a*: {analysis.labValues.a}, 
              b*: {analysis.labValues.b}
            </span></p>
            <p>Recommended Seasons: <span className="font-medium">
              {analysis.seasons.join(', ')}
            </span></p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SkinToneAnalyzer;
