// src/pages/Stats.js
import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'recharts';

const Stats = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statsView, setStatsView] = useState('daily'); // daily, weekly, monthly

  useEffect(() => {
    try {
      // Get data from localStorage
      const analysisHistory = JSON.parse(localStorage.getItem('analysisHistory') || '[]');
      setData(analysisHistory);
    } catch (err) {
      setError('Error loading analysis data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, []);

  if (loading) return <div className="p-8 text-center">Loading stats...</div>;
  if (error) return <div className="p-8 text-center text-red-600">{error}</div>;

  const downloadCSV = () => {
    try {
      const csv = convertToCSV(data);
      const blob = new Blob([csv], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `skin-tone-analysis-${new Date().toISOString()}.csv`;
      a.click();
    } catch (err) {
      setError('Error downloading data');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white rounded-xl shadow-lg p-6">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-2xl font-semibold">Analysis Statistics</h1>
            <button
              onClick={downloadCSV}
              className="bg-blue-100 hover:bg-blue-200 text-blue-600 px-4 py-2 rounded-lg"
            >
              Download Data
            </button>
          </div>

          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
            <div className="bg-pink-50 p-4 rounded-lg">
              <h3 className="text-lg font-medium">Total Analyses</h3>
              <p className="text-2xl">{data.length}</p>
            </div>
            <div className="bg-blue-50 p-4 rounded-lg">
              <h3 className="text-lg font-medium">Average Analysis Time</h3>
              <p className="text-2xl">
                {(data.reduce((acc, curr) => acc + curr.performanceMetrics.analysisTime, 0) / data.length).toFixed(2)}ms
              </p>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <h3 className="text-lg font-medium">Most Common Season</h3>
              <p className="text-2xl">
                {getMostCommonSeason(data)}
              </p>
            </div>
          </div>

          {/* Raw Data Table */}
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-4">Raw Data</h2>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Timestamp
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Season
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Undertone
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Lightness
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Analysis Time
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {data.map((item, index) => (
                    <tr key={index}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {new Date(item.timestamp).toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {item.seasons.join(', ')}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {item.undertone}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {item.lightness}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {item.performanceMetrics.analysisTime.toFixed(2)}ms
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const getMostCommonSeason = (data) => {
  const seasonCounts = data.reduce((acc, curr) => {
    curr.seasons.forEach(season => {
      acc[season] = (acc[season] || 0) + 1;
    });
    return acc;
  }, {});

  return Object.entries(seasonCounts)
    .sort(([,a], [,b]) => b - a)[0][0];
};

const convertToCSV = (data) => {
  const headers = ['Timestamp', 'Season', 'Undertone', 'Lightness', 'Analysis Time (ms)'];
  const rows = data.map(item => [
    item.timestamp,
    item.seasons.join(';'),
    item.undertone,
    item.lightness,
    item.performanceMetrics.analysisTime.toFixed(2)
  ]);

  return [
    headers.join(','),
    ...rows.map(row => row.join(','))
  ].join('\n');
};

export default Stats;
