import React, { useRef, useState, useEffect } from 'react';
import { Camera, History, RefreshCw, Info } from 'lucide-react';

const SkinToneAnalyzer = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [analysis, setAnalysis] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [history, setHistory] = useState([]);
  const [lightCondition, setLightCondition] = useState('unknown');
  const [error, setError] = useState(null);

  const SEASONAL_LIP_COLORS = {
    "Autumn": [
      { name: "Tall Poppy", color: "rgba(168,44,49,255)" },
      { name: "Turkish Rose", color: "rgba(187,107,113,255)" },
      { name: "Tonys Pink", color: "rgb(232,156,156)" }
    ],
    "Spring": [
      { name: "Deep Blush", color: "rgba(225,110,129,255)" },
      { name: "Valencia", color: "rgba(220,66,61,255)" },
      { name: "Mauvelous", color: "rgba(239,148,161,255)" }
    ],
    "Summer": [
      { name: "Night Shadz", color: "rgba(168,57,81,255)" },
      { name: "Mulberry", color: "rgba(206,75,124,255)" },
      { name: "Puce", color: "rgba(208,126,154,255)" }
    ],
    "Winter": [
      { name: "Medium Carmine", color: "rgba(173,58,67,255)" },
      { name: "Viola", color: "rgba(211,156,195,255)" },
      { name: "Charm", color: "rgba(210,123,141,255)" }
    ]
  };

  // Initialize webcam
  useEffect(() => {
    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: { width: 640, height: 480, facingMode: 'user' }
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        setError("Could not access camera. Please ensure camera permissions are granted.");
        console.error("Error accessing webcam:", err);
      }
    };
    startVideo();
  }, []);

  const detectLighting = (imageData) => {
    let totalBrightness = 0;
    for (let i = 0; i < imageData.data.length; i += 4) {
      const r = imageData.data[i];
      const g = imageData.data[i + 1];
      const b = imageData.data[i + 2];
      totalBrightness += (r + g + b) / 3;
    }
    const avgBrightness = totalBrightness / (imageData.data.length / 4);

    if (avgBrightness < 85) return 'dark';
    if (avgBrightness > 170) return 'bright';
    return 'good';
  };

  const getFitzpatrickScale = (L) => {
    if (L > 80) return 'Type I';
    if (L > 70) return 'Type II';
    if (L > 60) return 'Type III';
    if (L > 50) return 'Type IV';
    if (L > 40) return 'Type V';
    return 'Type VI';
  };

  const getSeasonsRecommendation = (undertone, lightness) => {
    let seasons = [];
    if (undertone === 'Warm') {
      if (lightness === 'High') {
        seasons = ['Spring', 'Autumn'];
      } else {
        seasons = ['Autumn'];
      }
    } else if (undertone === 'Cool') {
      if (lightness === 'High') {
        seasons = ['Summer', 'Winter'];
      } else {
        seasons = ['Winter'];
      }
    } else {
      if (lightness === 'High') {
        seasons = ['Spring', 'Summer'];
      } else {
        seasons = ['Autumn', 'Winter'];
      }
    }
    return seasons;
  };

  const getLipColorRecommendations = (seasons) => {
    let lipColors = [];
    seasons.forEach(season => {
      lipColors = [...lipColors, ...SEASONAL_LIP_COLORS[season]];
    });
    return lipColors;
  };

  const analyzeSkinTone = () => {
    setIsAnalyzing(true);
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    context.save();
    context.scale(-1, 1);
    context.translate(-canvas.width, 0);
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    context.restore();

    const sampleAreas = [
      { x: canvas.width/2 - 50, y: canvas.height/2 - 50, w: 100, h: 100 },
      { x: canvas.width/2 - 100, y: canvas.height/2 - 50, w: 50, h: 50 },
      { x: canvas.width/2 + 50, y: canvas.height/2 - 50, w: 50, h: 50 }
    ];

    let totalR = 0, totalG = 0, totalB = 0, totalPixels = 0;

    sampleAreas.forEach(area => {
      const imageData = context.getImageData(area.x, area.y, area.w, area.h);
      const lighting = detectLighting(imageData);
      setLightCondition(lighting);

      for (let i = 0; i < imageData.data.length; i += 4) {
        totalR += imageData.data[i];
        totalG += imageData.data[i + 1];
        totalB += imageData.data[i + 2];
        totalPixels++;
      }
    });

    const r = totalR / totalPixels;
    const g = totalG / totalPixels;
    const b = totalB / totalPixels;

    let rgb = [r/255, g/255, b/255];
    rgb = rgb.map(v => v > 0.04045 ? Math.pow((v + 0.055) / 1.055, 2.4) : v / 12.92);

    const X = rgb[0] * 0.4124 + rgb[1] * 0.3576 + rgb[2] * 0.1805;
    const Y = rgb[0] * 0.2126 + rgb[1] * 0.7152 + rgb[2] * 0.0722;
    const Z = rgb[0] * 0.0193 + rgb[1] * 0.1192 + rgb[2] * 0.9505;

    const Xn = 0.95047, Yn = 1.00000, Zn = 1.08883;
    const xyz = [X/Xn, Y/Yn, Z/Zn].map(v => 
      v > 0.008856 ? Math.pow(v, 1/3) : (7.787 * v) + 16/116
    );

    const L = (116 * xyz[1]) - 16;
    const aValue = 500 * (xyz[0] - xyz[1]);
    const bValue = 200 * (xyz[1] - xyz[2]);

    const hueAngle = Math.atan2(bValue, aValue) * (180/Math.PI);

    let undertone = '';
    if (hueAngle > 65) {
      undertone = 'Warm';
    } else if (hueAngle < 55) {
      undertone = 'Cool';
    } else {
      undertone = 'Neutral';
    }

    let lightness = '';
    if (L > 70) {
      lightness = 'High';
    } else if (L > 50) {
      lightness = 'Medium';
    } else {
      lightness = 'Low';
    }

    const seasons = getSeasonsRecommendation(undertone, lightness);
    const fitzpatrickType = getFitzpatrickScale(L);
    const lipColors = getLipColorRecommendations(seasons);

    const newAnalysis = {
      undertone,
      lightness,
      fitzpatrickType,
      seasons,
      lipColors,
      labValues: { L: L.toFixed(1), a: aValue.toFixed(1), b: bValue.toFixed(1) },
      timestamp: new Date().toISOString()
    };

    setAnalysis(newAnalysis);
    setHistory(prev => [...prev.slice(-4), newAnalysis]);
    setIsAnalyzing(false);
  };

  const getLightingAdvice = () => {
    switch(lightCondition) {
      case 'dark': return 'Lighting is too dark. Move to a brighter area for better results.';
      case 'bright': return 'Lighting is too bright. Reduce direct light for more accurate results.';
      case 'good': return 'Lighting conditions are optimal.';
      default: return 'Analyzing lighting conditions...';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-pink-100 to-pink-200">
      <div className="p-4 max-w-4xl mx-auto">
        {error && (
          <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
            <h4 className="font-bold">Error</h4>
            <p>{error}</p>
          </div>
        )}

        <div className="mb-4">
          <h2 className="text-2xl font-bold mb-2 text-gray-800">Seasonal Color Analysis</h2>
          <p className="text-gray-600">Center your face in the frame for accurate analysis</p>
          <div className="text-sm text-gray-500 flex items-center gap-1 mt-1">
            <Info size={14} />
            {getLightingAdvice()}
          </div>
        </div>

        <div className="relative mb-4">
          <video 
            ref={videoRef}
            autoPlay 
            playsInline
            className="w-full border rounded transform scale-x-[-1] bg-white"
          />
          <canvas 
            ref={canvasRef} 
            width="640" 
            height="480" 
            className="hidden"
          />
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
            <div className="w-[100px] h-[100px] border-2 border-white rounded-lg opacity-50" />
          </div>
        </div>

        <div className="flex gap-2 mb-4">
          <button
            onClick={analyzeSkinTone}
            disabled={isAnalyzing}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded flex items-center gap-2 disabled:opacity-50"
          >
            <Camera size={20} />
            {isAnalyzing ? 'Analyzing...' : 'Analyze Skin Tone'}
          </button>

          <button
            onClick={() => setHistory([])}
            className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded flex items-center gap-2"
          >
            <RefreshCw size={20} />
            Clear History
          </button>
        </div>

        {analysis && (
          <div className="space-y-4">
            <div className="p-4 border rounded bg-white/90 shadow-sm backdrop-blur-sm">
              <div className="mb-4">
                <h3 className="font-bold mb-2">Your Seasonal Analysis:</h3>
                <p className="text-lg font-medium">{analysis.seasons.join(' & ')} {analysis.seasons.length > 1 ? 'Seasons' : 'Season'}</p>
              </div>

              <div className="mb-4">
                <h3 className="font-bold mb-4">Recommended Lip Colors:</h3>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  {analysis.lipColors.map((color, i) => (
                    <div key={i} className="text-center space-y-2">
                      <div
                        className="w-16 h-16 rounded-full mx-auto shadow-sm border"
                        style={{ backgroundColor: color.color }}
                      />
                      <p className="text-sm font-medium">{color.name}</p>
                    </div>
                  ))}
                </div>
              </div>

              <div className="mt-4 pt-4 border-t">
                <p className="mb-2">Analysis Details:</p>
                <div className="space-y-1 text-sm text-gray-600">
                  <p>Undertone: <span className="font-medium">{analysis.undertone}</span></p>
                  <p>Lightness: <span className="font-medium">{analysis.lightness}</span></p>
                  <p>Fitzpatrick Scale: <span className="font-medium">{analysis.fitzpatrickType}</span></p>
                  <p>Lab Values: <span className="font-medium">
                    L*: {analysis.labValues.L}, 
                    a*: {analysis.labValues.a}, 
                    b*: {analysis.labValues.b}
                  </span></p>
                </div>
              </div>
            </div>

            {history.length > 0 && (
              <div className="p-4 border rounded bg-white/90 shadow-sm backdrop-blur-sm">
                <div className="flex items-center gap-2 mb-2">
                  <History size={20} />
                  <h3 className="font-bold">Analysis History:</h3>
                </div>
                <div className="space-y-2">
                  {history.map((record, i) => (
                    <div key={i} className="text-sm text-gray-600 border-b pb-2">
                      <p>Time: {new Date(record.timestamp).toLocaleTimeString()}</p>
                      <p>Season(s): {record.seasons.join(' & ')}</p>
                      <p>Undertone: {record.undertone}, Lightness: {record.lightness}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default SkinToneAnalyzer;
