import React, { useRef, useState, useEffect } from 'react';
import { Camera, History, RefreshCw, Info, Palette, Loader } from 'lucide-react';
// Import the background image
import pinkBg from '../assets/pink_gradient_bg.jpg';

const SkinToneAnalyzer = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [analysis, setAnalysis] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [history, setHistory] = useState([]);
  const [lightCondition, setLightCondition] = useState('unknown');
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const SEASONAL_LIP_COLORS = {
    "Autumn": [
      { name: "Tall Poppy", color: "rgba(168,44,49,255)" },
      { name: "Turkish Rose", color: "rgba(187,107,113,255)" },
      { name: "Tonys Pink", color: "rgb(232,156,156)" }
    ],
    "Spring": [
      { name: "Deep Blush", color: "rgba(225,110,129,255)" },
      { name: "Valencia", color: "rgba(220,66,61,255)" },
      { name: "Mauvelous", color: "rgba(239,148,161,255)" }
    ],
    "Summer": [
      { name: "Night Shadz", color: "rgba(168,57,81,255)" },
      { name: "Mulberry", color: "rgba(206,75,124,255)" },
      { name: "Puce", color: "rgba(208,126,154,255)" }
    ],
    "Winter": [
      { name: "Medium Carmine", color: "rgba(173,58,67,255)" },
      { name: "Viola", color: "rgba(211,156,195,255)" },
      { name: "Charm", color: "rgba(210,123,141,255)" }
    ]
  };

  // Initialize webcam
  // Initialize webcam with better error handling
  useEffect(() => {
    let stream = null;

    const startVideo = async () => {
      try {
        setIsLoading(true);
        stream = await navigator.mediaDevices.getUserMedia({ 
          video: { 
            width: 640, 
            height: 480, 
            facingMode: 'user',
            frameRate: 30
          }
        });

        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.onloadedmetadata = () => {
            setIsLoading(false);
          };
        }
      } catch (err) {
        setError(
          err.name === 'NotAllowedError' 
            ? "Camera access denied. Please enable camera permissions." 
            : "Could not access camera. Please check your device settings."
        );
        setIsLoading(false);
        console.error("Error accessing webcam:", err);
      }
    };
    startVideo();
   // Cleanup function
   return () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
  };
}, []);

  // Debounced lighting detection
  const detectLighting = React.useCallback((imageData) => {
    let totalBrightness = 0;
    for (let i = 0; i < imageData.data.length; i += 4) {
      totalBrightness += (
        imageData.data[i] * 0.299 + 
        imageData.data[i + 1] * 0.587 + 
        imageData.data[i + 2] * 0.114
      );
    }
    const avgBrightness = totalBrightness / (imageData.data.length / 4);

    if (avgBrightness < 85) return 'dark';
    if (avgBrightness > 170) return 'bright';
    return 'good';
  }, []);

  const getFitzpatrickScale = (L) => {
    if (L > 80) return 'Type I';
    if (L > 70) return 'Type II';
    if (L > 60) return 'Type III';
    if (L > 50) return 'Type IV';
    if (L > 40) return 'Type V';
    return 'Type VI';
  };

  const getSeasonsRecommendation = (undertone, lightness) => {
    let seasons = [];
    if (undertone === 'Warm') {
      if (lightness === 'High') {
        seasons = ['Spring', 'Autumn'];
      } else {
        seasons = ['Autumn'];
      }
    } else if (undertone === 'Cool') {
      if (lightness === 'High') {
        seasons = ['Summer', 'Winter'];
      } else {
        seasons = ['Winter'];
      }
    } else {
      if (lightness === 'High') {
        seasons = ['Spring', 'Summer'];
      } else {
        seasons = ['Autumn', 'Winter'];
      }
    }
    return seasons;
  };

  const getLipColorRecommendations = (seasons) => {
    let lipColors = [];
    seasons.forEach(season => {
      lipColors = [...lipColors, ...SEASONAL_LIP_COLORS[season]];
    });
    return lipColors;
  };

  // Enhanced skin tone analysis with validation
  const analyzeSkinTone = async () => {
    if (!videoRef.current || !canvasRef.current) {
      setError("Camera not ready. Please wait or refresh the page.");
      return;
    }

    setIsAnalyzing(true);
    try {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');

      context.save();
      context.scale(-1, 1);
      context.translate(-canvas.width, 0);
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      context.restore();

      const sampleAreas = [
        { x: canvas.width/2 - 50, y: canvas.height/2 - 50, w: 100, h: 100 },
        { x: canvas.width/2 - 100, y: canvas.height/2 - 50, w: 50, h: 50 },
        { x: canvas.width/2 + 50, y: canvas.height/2 - 50, w: 50, h: 50 }
      ];

      let totalR = 0, totalG = 0, totalB = 0, totalPixels = 0;

      sampleAreas.forEach(area => {
        const imageData = context.getImageData(area.x, area.y, area.w, area.h);
        const lighting = detectLighting(imageData);
        setLightCondition(lighting);

        for (let i = 0; i < imageData.data.length; i += 4) {
          totalR += imageData.data[i];
          totalG += imageData.data[i + 1];
          totalB += imageData.data[i + 2];
          totalPixels++;
        }
      });

      const r = totalR / totalPixels;
      const g = totalG / totalPixels;
      const b = totalB / totalPixels;

      let rgb = [r/255, g/255, b/255];
      rgb = rgb.map(v => v > 0.04045 ? Math.pow((v + 0.055) / 1.055, 2.4) : v / 12.92);

      const X = rgb[0] * 0.4124 + rgb[1] * 0.3576 + rgb[2] * 0.1805;
      const Y = rgb[0] * 0.2126 + rgb[1] * 0.7152 + rgb[2] * 0.0722;
      const Z = rgb[0] * 0.0193 + rgb[1] * 0.1192 + rgb[2] * 0.9505;

      const Xn = 0.95047, Yn = 1.00000, Zn = 1.08883;
      const xyz = [X/Xn, Y/Yn, Z/Zn].map(v => 
        v > 0.008856 ? Math.pow(v, 1/3) : (7.787 * v) + 16/116
      );

      const L = (116 * xyz[1]) - 16;
      const aValue = 500 * (xyz[0] - xyz[1]);
      const bValue = 200 * (xyz[1] - xyz[2]);

      const hueAngle = Math.atan2(bValue, aValue) * (180/Math.PI);

      let undertone = '';
      if (hueAngle > 65) {
        undertone = 'Warm';
      } else if (hueAngle < 55) {
        undertone = 'Cool';
      } else {
        undertone = 'Neutral';
      }

      let lightness = '';
      if (L > 70) {
        lightness = 'High';
      } else if (L > 50) {
        lightness = 'Medium';
      } else {
        lightness = 'Low';
      }

      const seasons = getSeasonsRecommendation(undertone, lightness);
      const fitzpatrickType = getFitzpatrickScale(L);
      const lipColors = getLipColorRecommendations(seasons);

      // Add validation for the analysis results
      if (L < 0 || L > 100) {
        throw new Error("Invalid analysis results. Please try again with better lighting.");
      }  

      const newAnalysis = {
        undertone,
        lightness,
        fitzpatrickType,
        seasons,
        lipColors,
        labValues: { 
          L: L.toFixed(1), 
          a: aValue.toFixed(1), 
          b: bValue.toFixed(1) 
        },
        timestamp: new Date().toISOString()
      };

      setAnalysis(newAnalysis);
      setHistory(prev => [...prev.slice(-4), newAnalysis]);
      setError(null);
    } catch (err) {
      setError("Analysis failed. Please try again with better lighting conditions.");
      console.error("Analysis error:", err);
    } finally {
      setIsAnalyzing(false);
    }
  };


  return (
    <div 
      style={{
        backgroundImage: `url(${pinkBg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
        width: '100%'
      }}
      className="p-4 sm:p-8"
    >
      <div className="max-w-6xl mx-auto">
        {/* Title */}
        <h1 className="text-3xl sm:text-4xl font-light italic text-red-500 mb-8">
          Extracting Your Colors...
        </h1>

        {/* Error Display */}
        {error && (
          <div className="mb-4 p-4 bg-red-50 border-l-4 border-red-500 text-red-700 rounded">
            <p className="flex items-center gap-2">
              <Info size={16} />
              {error}
            </p>
          </div>
        )}

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Left Panel - Camera */}
          <div className="flex-1">
            <div className="bg-white rounded-3xl p-6 shadow-lg">        
              <div className="relative min-h-[320px]">
                {isLoading ? (
                  <div className="absolute inset-0 flex items-center justify-center">
                    <Loader className="w-8 h-8 animate-spin text-red-500" />
                  </div>
                ) : (
                  <>
                    <video 
                      ref={videoRef}
                      autoPlay 
                      playsInline
                      className="w-full rounded-xl transform scale-x-[-1]"
                    />
                    <canvas 
                      ref={canvasRef} 
                      width="640" 
                      height="480" 
                      className="hidden"
                    />
                    <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-80 border-2 border-white rounded-full"/>
                  </>
                )}
              </div>

              <div className="mt-4 space-y-4">
                <p className="text-center text-xl">
                  Place your face in the guide and face the front.
                </p>

                <div className="flex justify-center gap-4">
                  <button
                    onClick={analyzeSkinTone}
                    disabled={isAnalyzing || isLoading}
                    className="bg-red-100 hover:bg-red-200 text-red-600 px-6 py-3 rounded-full flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    {isAnalyzing ? (
                      <Loader className="w-5 h-5 animate-spin" />
                    ) : (
                      <Camera size={20} />
                    )}
                    {isAnalyzing ? 'Analyzing...' : 'Analyze'}
                  </button>

                  {history.length > 0 && (
                    <button
                      onClick={() => setHistory([])}
                      className="bg-gray-100 hover:bg-gray-200 text-gray-600 px-6 py-3 rounded-full flex items-center gap-2 transition-colors"
                    >
                      <RefreshCw size={20} />
                      Clear History
                    </button>
                  )}
                </div>

              </div>                
            </div>
          </div>

          {/* Right Panel - Results */}
          {analysis && (
            <div className="lg:w-96">
              <div className="bg-white rounded-3xl p-6 shadow-lg space-y-8">
                {/* Color Palette Section */}
                <div>
                  <div className="flex items-center gap-2 mb-2">
                    <Palette size={20} className="text-gray-600" />
                    <span className="text-gray-600">Color Palette</span>
                  </div>
                  <h2 className="text-3xl font-serif">{analysis.seasons[0]}</h2>
                </div>

                {/* Hue Section */}
                <div>
                  <div className="flex items-center gap-2 mb-2">
                    <div className="w-5 h-5 rounded-full bg-gradient-to-r from-yellow-400 to-red-400" />
                    <span className="text-gray-600">Hue</span>
                  </div>
                  <h2 className="text-3xl font-serif">{analysis.undertone} Tone</h2>
                </div>

                {/* Analysis Results */}
                <div className="border-t pt-6">
                  <h3 className="text-xl font-bold mb-4">Your Seasonal Analysis:</h3>
                  <div className="space-y-6">
                    {/* Lip Colors */}
                    <div>
                      <h4 className="font-bold mb-3">Recommended Lip Colors:</h4>
                      <div className="grid grid-cols-3 gap-4">
                        {analysis.lipColors.map((color, i) => (
                          <div key={i} className="text-center">
                            <div 
                              className="w-12 h-12 rounded-full mx-auto mb-2 shadow-md border-2 border-white transition-transform hover:scale-110"
                              style={{ backgroundColor: color.color }}
                            />
                            <span className="text-xs">{color.name}</span>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Details */}
                    <div>
                      <h4 className="font-bold mb-2">Analysis Details:</h4>
                      <div className="space-y-1 text-sm">
                        <p>Undertone: <span className="font-medium">{analysis.undertone}</span></p>
                        <p>Lightness: <span className="font-medium">{analysis.lightness}</span></p>
                        <p>Fitzpatrick Scale: <span className="font-medium">{analysis.fitzpatrickType}</span></p>
                        <p>Lab Values: L*: {analysis.labValues.L}, a*: {analysis.labValues.a}, b*: {analysis.labValues.b}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SkinToneAnalyzer;
